define([
    'lodash'
],
function(_) {
    var TestTarget = function(name, options) {
        this.name = name;
        this._options = {
            setUp: function() {},
            tearDown: function() {}
        };

        _.extend(this._options, options);
    };

    TestTarget.prototype.setUp = function() {
        this._options.setUp();
    };

    TestTarget.prototype.tearDown = function() {
        this._options.tearDown();
    };

    return TestTarget;
});
