define([
    'mobifyjs/utils',
    'adaptivejs/utils/general',
    'adaptivejs/utils/assets',
    'adaptivejs/utils/buildScript',
    'adaptivejs/utils/buildOrigin',
    'adaptivejs/utils/isCapturing',
    'adaptivejs/utils/isDebug',
    'adaptivejs/utils/isLocal',
], function(mobifyJsUtils, generalUtils, assetUtils, buildScript, buildOrigin, isCapturing, isDebug, isLocal) {

    var AdaptiveUtils = {};

    mobifyJsUtils.extend(AdaptiveUtils, mobifyJsUtils, generalUtils, assetUtils, {
        getBuildScript: buildScript,
        getBuildOrigin: buildOrigin,
        isCapturing: isCapturing,
        isDebug: isDebug,
        isLocal: isLocal
    });

    return AdaptiveUtils;
});
