define([
    'lib/viewMocker',
    'pages/<%= viewName %>/<%= viewName %>-view',
    'text!fixtures/<%= viewName %>.html',
    'chai'
],
function(test, view, fixture, chai) {
    var expect = chai.expect;

    test('<%= viewName %> view context', view, fixture, {
        'context contains the correct template name': function($, context) {
            var templateName = context.templateName;
            expect(templateName).to.equal('<%= viewName %>', '<%= viewName %> context has correct template name');
        }
    });

    test('<%= viewName %> view DOM', view, fixture, {
        'adaptation adds the correct template class': function($) {
            var $body = $('body').last();

            expect($body.hasClass('t-<%= viewName %>')).to.be.true;
        }
    });
});
